package leetcode;

public class NumberofSubarraysofSizeKandAverageGreaterthanorEqualtoThreshold1343 {

    public void NumberofSubarraysofSizeKandAverageGreaterthanorEqualtoThreshold1343() {
        int[] arr = {2, 2, 2, 2, 5, 5, 5, 8};
        int k = 3;
        int threshold = 4;
        int iCounterGreater = 0;
        int iSumK = 0;
        for (int i = 0; i < k; i++) {
            iSumK += arr[i];
        }
        if ((iSumK / k) >= threshold) {
            iCounterGreater++;
        }
        for (int i = k; i < arr.length; i++) {
            iSumK += arr[i];
            iSumK -= arr[i - k];
            if ((iSumK / k) >= threshold) {
                iCounterGreater++;
            }
        }
        System.out.println(iCounterGreater);
    }
}
